package org.example;

import org.example.controller.ArticleController;
import org.example.controller.SystemController;
import org.example.repository.ArticleRepository;
import org.example.service.ArticleService;

import java.time.format.DateTimeFormatter;
import java.util.Scanner;

public class AppContext {
    public static final Scanner scanner;
    public static final DateTimeFormatter forPrintDateTimeFormatter;
    public static final WiseSayingRepository wiseSayingRepository;
    public static final WiseSayingService wiseSayingService;
    public static final WiseSayingController wiseSayingController;
    public static final SystemController systemController;

    static {
        scanner = new Scanner(System.in);
        forPrintDateTimeFormatter = DateTimeFormatter.ofPattern("yy-MM-dd HH:mm:ss");
        wiseSayingRepository = new WiseSayingRepository();
        wiseSayingService = new WiseSayingService();
        wiseSayingController = new WiseSayingController();
        systemController = new SystemController();
    }
}

package org.example;

import org.example.controller.ArticleController;
import org.example.controller.SystemController;
import org.example.repository.ArticleRepository;
import org.example.service.ArticleService;

import java.time.format.DateTimeFormatter;
import java.util.Scanner;

public class AppContext {
    public static final Scanner scanner;
    public static final DateTimeFormatter forPrintDateTimeFormatter;
    public static final ArticleRepository articleRepository;
    public static final ArticleService articleService;
    public static final ArticleController articleController;
    public static final SystemController systemController;

    static {
        scanner = new Scanner(System.in);
        forPrintDateTimeFormatter = DateTimeFormatter.ofPattern("yy-MM-dd HH:mm:ss");
        articleRepository = new ArticleRepository();
        articleService = new ArticleService();
        articleController = new ArticleController();
        systemController = new SystemController();
    }
}

package org.example.controller;

import org.example.AppContext;
import org.example.entity.Article;
import org.example.service.ArticleService;

import java.util.List;

public class ArticleController {
    ArticleService service = AppContext.articleService;

    public Article getArticle(int id) throws Exception {
        return service.getArticle(id);
    }

    public List<Article> getArticleList(String order, String keyword, int page) throws Exception {
        List<Article> list = service.getArticleList(order, keyword, page, 5);
        return list;
    }

    public int createArticle(String title, String content) throws Exception {
        return service.createArticle(title, content);
    }

    public int updateArticle(int id, String title, String content) throws Exception {
        return service.updateArticle(id, title, content);
    }

    public int deleteArticle(int id) throws Exception {
        return service.deleteArticle(id);
    }
}

